import logo from './logo.svg';
import './App.css';
import React, {useState, useEffect} from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.css'

function App() {
const [animal, setAnimal] = useState([])
const [name, setName] = useState()
const [species, setSpecies] = useState()
const [image, setImage] = useState()
const[upForAdoption, setUpForAdoption] = useState(true)

//=====ON CHANGE=====//

const handleChangeName = (e)=>{
  setName(e.target.value)
}
const handleChangeSpecies = (e)=>{
  setSpecies(e.target.value)
}
const handleChangeImage = (e)=>{
  setImage(e.target.value)
}
const handleChangeAdoption =(e)=>{
  setUpForAdoption(e.target.checked)
}

//=====AXIOS=====//

//____FETCH
const getAnimalData = ()=>{
  axios.get('http://localhost:3000/animal').then((response)=>{
    setAnimal(response.data);
  })
}
useEffect(()=>{
  getAnimalData()
 },[])
//____POST

const addAnimal =()=>{
  axios.post('http://localhost:3000/animal',{
    name: name,
    species: species,
    image: image,
    reservedForAdoption: upForAdoption
}).then(()=>{axios.get('http://localhost:3000/animal').then((response)=>{
  setAnimal(response.data)
})
})
}



//____DELETE
 const deleteAnimal = (animal)=>{
   axios.delete(`http://localhost:3000/animal/${animal._id}`).then(()=>{
    axios.get('http://localhost:3000/animal').then((response)=>{
      setAnimal(response.data)
   })
  })
 }
//____UPDATE
const updateAnimals = (event, animalsData) =>{
  event.preventDefault()
  axios.put(`http://localhost:3000/animal/${animalsData._id}`,

  {
    name: name,
    species: species,
    image: image,
    reservedForAdoption: upForAdoption
  }

  ).then(()=>{axios.get('http://localhost:3000/animal').then((response)=>{
    // console.log(response.data)
    setAnimal(response.data)
  })
})

}

