          Up for Adoption: <input type ="checkbox" /><br/>
          <input className ="btn btn-primary" type ="submit" value="Add to the list" />

   </form>
   </section>

     <h2>Waiting to go Home...</h2>

     <div className='row'>
     {animals.map((animal)=>{
        return (
        <div className='col-sm-6'  key={animal._id} >

        <h2>{animal.name}</h2>
        <h4>{animal.name} is a {animal.species}!</h4>
        <img className ="img-thumbnail" src={animal.image} />



 <form onSubmit={(event)=>{updateAnimals(event, animal)}}>
   <div className='mb-3'>
 <label>Name: <input className='form-control' type="text" defaultValue={animal.name} onChange={newName} /></label><br/>
 <label> Species: <input className='form-control' type="text" defaultValue={animal.species} onChange={newSpecies} /></label><br/>
 <label>Image: <input className='form-control' type="url" defaultValue={animal.image} onChange={newImage} /></label><br/>


  <input className ="btn btn-primary" type="submit" value="Update" />
  <button className ="btn btn-primary" onClick={(event)=>{deleteAnimals(animal)}}>Found New Home</button>
  </div>
</form>
</div>

