import logo from './logo.svg';
import './App.css';
import{useState, useEffect} from 'react'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.css'

function App() {
const[name, setName] = useState("")
const[species, setSpecies] = useState("")
const[image, setImage] = useState("")
const[upForAdoption, setUpForAdoption] = useState(true)
const [animals, setAnimals] = useState([])


//_______________FORM
const newName = (event) =>{
    setName(event.target.value)
}
const newSpecies = (event) =>{
  setSpecies(event.target.value)
}
const newImage = (event) =>{
  setImage(event.target.value)
}

const adoption =(event)=>{
  setUpForAdoption(event.target.checked)
}
//__________________________

//_____________CREATE

const addAnimal = (event) =>{
event.preventDefault();
axios.post('http://localhost:3000/animals', 
{
  name: name,
  species: species,
  image: image,
  reservedForAdoption: upForAdoption
}).then(()=>{axios.get('http://localhost:3000/animals').then((response)=>{
  setAnimals(response.data)
})
})
}
useEffect(()=>{
  axios.get('http://localhost:3000/animals').then((response)=>{
        setAnimals(response.data);
      })
},[])

//______DELETE
const deleteAnimals = (animalsData) =>{
  axios.delete(`http://localhost:3000/animals/${animalsData._id}`).then(()=>{axios.get('http://localhost:3000/animals').then((response)=>{
    setAnimals(response.data)
  })
})
}
//______UPDATE

const updateAnimals = (event, animalsData) =>{
  event.preventDefault()
  axios.put(`http://localhost:3000/animals/${animalsData._id}`,

  {
    name: name,
    species: species,
    image: image,

  }

  ).then(()=>{axios.get('http://localhost:3000/animals').then((response)=>{
    // console.log(response.data)
    setAnimals(response.data)
  })
})

}



  return (
   <>
   <header><h1>Sunny Animal Shelter</h1></header>

   <div className='container'>
   <section className="create">
     <h2>Create New Animal Profile</h2>

   <form onSubmit={addAnimal}>

          Name: <input className='form-control' type="text" onChange={newName} /><br/>
          Species: <input className='form-control' type="text" onChange={newSpecies} /><br/>
          Image: <input className='form-control' type="url" onChange={newImage} /><br/>
          Up for Adoption: <input type ="checkbox" /><br/>
          <input className ="btn btn-primary" type ="submit" value="Add to the list" />

   </form>
   </section>

     <h2>Waiting to go Home...</h2>

     <div className='row'>
     {animals.map((animal)=>{
        return (
        <div className='col-sm-6'  key={animal._id} >

        <h2>{animal.name}</h2>
        <h4>{animal.name} is a {animal.species}!</h4>
        <img className ="img-thumbnail" src={animal.image} />



 <form onSubmit={(event)=>{updateAnimals(event, animal)}}>
   <div className='mb-3'>
 <label>Name: <input className='form-control' type="text" defaultValue={animal.name} onChange={newName} /></label><br/>
 <label> Species: <input className='form-control' type="text" defaultValue={animal.species} onChange={newSpecies} /></label><br/>
 <label>Image: <input className='form-control' type="url" defaultValue={animal.image} onChange={newImage} /></label><br/>


  <input className ="btn btn-primary" type="submit" value="Update" />
  <button className ="btn btn-primary" onClick={(event)=>{deleteAnimals(animal)}}>Found New Home</button>
  </div>
</form>
</div>

      ) })}
</div>
   </div>
   </>
  );
}

export default App;
